const path = require('path')
const html = `
<!DOCTYPE html>
	<html lang="en">
	<head>
	  <meta charset="UTF-8">
	  <meta name="viewport" content="width=device-width">
	  <title>🌐 sir dive alot</title>
	  <link href="https://fonts.googleapis.com/css?family=Inconsolata" rel="stylesheet">
	  <link href="${path.join(process.env.dir, '/css/default.css')}" rel="stylesheet" />

`

function make_link(css) {
	return `<link href="${path.join(process.env.dir, css)}" rel="stylesheet" />`
}

module.exports = function(styles) {
	var links = (styles || []).map(make_link).join('\n')
	return html + links + "</head>"
}