const path = require('path')

const pages = [
	{ name: 'Home' , anchor: "/index.html"       },
	{ name: 'Blog' , anchor: "/blog/index.html"  },
	{ name: 'Gear' , anchor: "/gear/index.html"  },
	{ name: 'Dives', anchor: "/dives/index.html" },
	{ name: 'World', anchor: "/world/index.html" },
]

module.exports = function(active_page) {

	var links = pages.map(page => {
		const active = page.name === active_page ? "nav-active" : ""
		const link   = path.join(process.env.dir, page.anchor)
		return `<a href=${link} class="${active}">${page.name}</a>`
	})

	const nav = `
	<div class="container">
		<nav>
			<div class="nav-left">
				${links[0]}
			</div>
			<div class="nav-right">
				${links.slice(1).join('\n')}
			</div>
		</nav>
	</div>
`
	return nav
}